<script>
  export default {
    data () {
      return {
//        catalogs: []
        catalogs:""
      }
    },
    mounted () {
      this.init();
    },
    methods:{
      init() {
        var body = $('#article_body')
        var header = body.find(':header')   //取得标题数组
        var catalog = ''                    //目标的html文本,根据描扫后的结果增加
        var autoid  = 0;                    //为标题自增id，h1加100，h2加10，h3加1
        catalog += '<ul class="nav" style="width:300px;zoom:1">'
        catalog += '<li><span style="font-size: 12pt;">索引</span></li>'
        header.each(function (t){
          var u = $(this), v = u[0];
          var title=u.text();
          var text=u.text();
          console.log("title:",title);
          if (v.localName === 'h1') {
            autoid = (parseInt(autoid/10)+1)*100; //向上取整百。例如，当前id为340，运算后，则变为400
            u.attr('id',autoid);
            if(text.length>26) text=text.substr(0,26)+"...";  //一级标题长度
            catalog += '<li><a href="#' + u.attr('id') + '" title="' + title + '">' + text + '</a><span class="sideCatalog-dot"></span></li>';
          } else if (v.localName === 'h2') {
            autoid += 10;
            u.attr('id',autoid);
            if(text.length>30) text=text.substr(0,30)+"...";  //二级标题长度
            catalog += '<li class="h2Offset"><a href="#' + u.attr('id') + '" title="' + title + '">' + text + '</a></li>';
          }else if(v.localName == 'h3'){
            autoid += 1;
          }
        });
        catalog +='</ul>'
        console.log("sideCatalog",catalog)
        this.catalogs = catalog;

        $('#article_content').on('scroll', function (){
          console.log("scroll-----")
          var t = body.scrollTop();
          if (t > 200) {
            $('#sideToolbar').css('display', 'block')
          } else {
            $('#sideToolbar').css('display', 'none')
          }
        })
      }
    }
  }
</script>

